mapper.select:
select cn_share_id, cn_share_title,cn_share_body,cn_note_id from cn_share where cn_share_title like #{0}   union
select cn_note_id as 'cn_share_id',cn_note_title as  'cn_share_title',cn_note_body as  'cn_share_body' ,cn_note_id
 from cn_note where cn_note_title like #{1}

	 	select *from cn_share where cn_share_id =#{0} union
select cn_note_id as 'cn_share_id',cn_note_title as  'cn_share_title',cn_note_body as  'cn_share_body' ,cn_note_id
 from cn_note where cn_note_id = #{1}
